mapper.select:
SELECT p1.permission_id AS permissionId, p1.parent_id AS parentId, p1.per_name AS perName, p1.per_code AS perCode, p1.uri AS uri, p1.icon_name AS iconName, p1.per_type AS perType, p1.per_level AS perLevel, p1.status AS status, p1.create_time AS createTime, p1.modify_time AS modifyTime, p1.description AS description, p1.create_by AS createBy, p1.create_time AS createTime, IFNULL(p2.per_name,'cms') AS parentName FROM t_sys_permission p1 LEFT JOIN t_sys_permission p2 ON p1.parent_id = p2.permission_id
ORDER BY p1.per_level,p1.create_time
LIMIT #{start},#{limit}
